using UnityEngine; using UnityEngine.UI;  public class Score : MonoBehaviour {      // スコアを表示する     public GameObject pointText;      // スコア     private int Point;       void Start()     {         //シーン中のScoreオブジェクトを取得         this.pointText = GameObject.Find("Score");             }      void Update()     {      }         // 衝突の検出     void OnCollisionEnter(Collision collision)     {         Debug.Log("OnCollision");          if (collision.gameObject.tag == "LargeStarTag")         {             Point += 20;             Debug.Log("Point20:" + Point);             //pointText.text = Point.ToString() + "点";             this.pointText.GetComponent<Text>().text = Point.ToString() + "点";         }         if (collision.gameObject.tag == "SmallStarTag")         {             Point += 10;             Debug.Log("Point10:" + Point);             this.pointText.GetComponent<Text>().text = Point.ToString() + "点";         }     } }  